mapper.select:
select user_id, user_name,
user_role, user_pass, user_email,user_phone,user_create_date from assetuser where
user_id=#{userId}

mapper.update:
update assetuser set
user_pass=#{userPass}
,user_name=#{userName}
,user_email=#{userEmail}
,user_phone=#{userPhone}
where user_id=#{userId}

update assetuser set
user_name=#{userName}
,user_email=#{userEmail}
,user_phone=#{userPhone}
where user_id=#{userId}
